var Brick = require("../../../../../");
var Footer = require('footer');

var Photo = Brick({
  show: show
});

module.exports = Photo;

function show (photo) {
  photo.brick.bind('.photo', {
    href: photo.urls.large,
    style: 'background-image:url(' + photo.urls.medium + ')',
    _html: Footer.New({
      title: photo.title
    }),
    'data-title': photo.title || "no title",
    'data-url': photo.urls.large
  });
}
